import json
import logging
import os

import imgkit
from django.conf import settings

import library.djangohuey as huey
from core.models import Datasource
from core.utils import database
from whatsapp.models import Outbox, Session

logger = logging.getLogger(__name__)


@huey.db_task(name='Generate Work Order Task', )
def process():
    datasource = Datasource.objects.get(pk='CRM.NBI')
    actors = database.fetchall(datasource.connection(), '''
        SELECT
            COALESCE(TRIM(a.userid), '') AS actor,
            CONCAT(REGEXP_REPLACE(
                CASE LEFT(COALESCE(TRIM(a.phonenumber), '082266331136'), 1)
                    WHEN '0' THEN CONCAT('62', RIGHT(COALESCE(TRIM(a.phonenumber), '082266331136'), -1))
                    ELSE COALESCE(TRIM(a.phonenumber), '082266331136')
                END, '[^\\w]+', '', 'g'), '@s.whatsapp.net') AS actorjid
        FROM sc_mst.user a WHERE TRUE
        AND COALESCE(TRIM(a.phonenumber), '') NOT IN ('')
        AND CONCAT('WORKORDER:UPDATE:', COALESCE(TRIM(a.userid), '')) IN (
            SELECT COALESCE(TRIM(b.parameter), '')
            FROM sc_mst.setup b WHERE TRUE
            AND TRIM(b.parameter) ILIKE '%WORKORDER:UPDATE:%'
            AND TRIM(b.value)::CHAR = 'Y'
        )
        ''', )
    workorders = database.fetchall(datasource.connection(), '''
        SELECT * FROM (
            SELECT
                COALESCE(TRIM(a.docno), '') AS document,
                a.docdate AS createdate,
                TO_CHAR(a.docdate, 'dd-mm-yyyy hh24:mi:ss') AS createdateformat,
                COALESCE(TRIM(a.nopol), '') AS platenumber,
                COALESCE(TRIM(a.sopirid), '') AS driverid,
                CASE WHEN LENGTH(REGEXP_REPLACE(b.userlname, '[^\\w]+', '', 'g')) > 0 THEN CONCAT(TRIM(b.userlname), ' / ', COALESCE(TRIM(b.phonenumber), '082266331136'))
                ELSE CONCAT(TRIM(b.usersname), ' / ', COALESCE(TRIM(b.phonenumber), '082266331136')) END AS drivername,
                CONCAT(REGEXP_REPLACE(
                CASE LEFT(COALESCE(TRIM(b.phonenumber), '082266331136'), 1)
                    WHEN '0' THEN CONCAT('62', RIGHT(COALESCE(TRIM(b.phonenumber), '082266331136'), -1))
                    ELSE COALESCE(TRIM(b.phonenumber), '082266331136')
                END, '[^\\w]+', '', 'g'), '@s.whatsapp.net') AS driverjid,
                a.docsjstartdate AS begindate,
                TO_CHAR(a.docsjstartdate, 'dd-mm-yyyy hh24:mi:ss') AS begindateformat,
                COALESCE(TRIM(a.docsjref), '') AS travel,
                COALESCE(TRIM(a.citycodefrom), '') AS sourceid,
                COALESCE(TRIM(c.areaname), '') AS sourcename,
                a.citycodeto AS target,
                COALESCE(TRIM(d.areaname), '') AS targetname,
                REGEXP_REPLACE(COALESCE(TRIM(a.note), ''), E'[\\n\\r]+', '', 'g' ) AS note,
                COALESCE(TRIM(a.lastchangeby), '') AS lastchangeby,
                CASE WHEN LENGTH(REGEXP_REPLACE(e.userlname, '[^\\w]+', '', 'g')) > 0 THEN TRIM(e.userlname)
                ELSE TRIM(e.usersname) END AS lastchangebyname,
                a.lastchangedate AS lastchangedate,
                TO_CHAR(a.lastchangedate, 'dd-mm-yyyy hh24:mi:ss') AS lastchangedateformat
            FROM sc_trx.roadcostdriver a
            LEFT JOIN sc_mst.user b ON TRUE
            AND TRIM(b.branch) = TRIM(a.branch)
            AND TRIM(b.userid) = TRIM(a.sopirid)
            LEFT JOIN sc_mst.areamst c ON TRUE
            AND TRIM(c.branch) = TRIM(a.branch)
            AND TRIM(c.areacode) = TRIM(a.citycodefrom)
            LEFT JOIN sc_mst.areamst d ON TRUE
            AND TRIM(d.branch) = TRIM(a.branch)
            AND TRIM(d.areacode) = TRIM(a.citycodeto)
            LEFT JOIN sc_mst.user e ON TRUE
            AND TRIM(e.branch) = TRIM(a.branch)
            AND TRIM(e.userid) = TRIM(a.lastchangeby)
            WHERE TRUE
            AND TRIM(a.status) = 'P'
            AND COALESCE(TRIM(a.status), '') NOT IN ('')
            AND COALESCE(TRIM(a.docsjref), '') NOT IN ('')
            AND NOT a.senttoactor
            ORDER BY document
        ) AS a WHERE TRUE
        ''', )
    if actors is not None and len(actors) > 0 and workorders is not None and len(workorders) > 0:
        session = Session.objects.get(pk='nusantarabot.id')
        for workorder in workorders:
            if not os.path.exists(os.path.join(settings.BASE_DIR, 'data/generated')):
                os.makedirs(os.path.join(settings.BASE_DIR, 'data/generated'))
            image = os.path.join(settings.BASE_DIR, f'data/generated/{workorder.document}.jpg', )
            if not os.path.exists(image):
                imgkit.from_string(f'''
                    <table background='data:image/png;base64,'>
                    <thead>
                      <tr>
                        <th colspan='3'>.: Surat Perintah Kerja :.</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td valign='top'><b>Nomer</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.document}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Tanggal</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.createdateformat}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>No.Polisi</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.platenumber}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Driver</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.drivername}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Tgl. Berangkat</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.begindateformat}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Surat Jalan</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.travel}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Dari</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.sourcename}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Tujuan</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.targetname}</td>
                      </tr>
                      <tr>
                        <td valign='top'><b>Catatan</b></td>
                        <td valign='top'>:</td>
                        <td valign='top'>{workorder.note}</td>
                      </tr>
                    </tbody>
                    </table>
                    ''', image, options=dict(format='jpg', width=400, ))
            for actor in actors:
                Outbox.objects.create(
                    message=json.dumps(dict(
                        path=image.replace('\\', '/'),
                        caption=f'Surat Perintah Kerja *{workorder.document}*'
                    )),
                    message_type='imageMessage',
                    outbox_for=actor.actorjid,
                    session=session,
                    properties=dict(
                        actor=actor.actor,
                    ),
                )
            Outbox.objects.create(
                message=json.dumps(dict(
                    path=image.replace('\\', '/'),
                    caption=f'Surat Perintah Kerja *{workorder.document}*'
                )),
                message_type='imageMessage',
                outbox_for=workorder.driverjid,
                session=session,
                properties=dict(
                    driver=workorder.driverid,
                ),
            )
            database.update(datasource.connection(), '''
                UPDATE sc_trx.roadcostdriver SET
                    senttoactor = TRUE,
                    lastsenttoactordate = NOW()
                WHERE TRUE
                AND TRIM(docno) = %(document)s::VARCHAR
                ''', workorder)
